import React, { Component } from "react";
import "./RollDice.css";
import Die from "../Die/Die.js";
import Steps from "../steps/Steps";

class RollDice extends Component {
  // Face numbers passes as default props
  static defaultProps = {
    sides: ["one", "two", "three", "four", "five", "six"],
  };
  constructor(props) {
    super(props);

    // States
    this.state = {
      die: "one",
      rolling: false,
    };
    this.roll = this.roll.bind(this);
  }
  roll() {
    const { sides } = this.props;
    this.setState({
      // Changing state upon click
      die: sides[Math.floor(Math.random() * sides.length)],
      rolling: true,
    });

    // Start timer of one sec when rolling start
    setTimeout(() => {
      // Set rolling to false again when time over
      this.setState({ rolling: false });
    }, 1000);
  }

  render() {
    const handleBtn = this.state.rolling ? "RollDice-rolling" : "";
    const { die, rolling } = this.state;
    return (
      <div className="RollDice">
        <Steps />
        <div className="dice-button">
          <div className="RollDice-container">
            <Die face={die} rolling={rolling} />
          </div>
          <button
            className={handleBtn}
            disabled={this.state.rolling}
            onClick={this.roll}
          >
            {this.state.rolling ? "Rolling" : "Roll Dice!"}
          </button>
        </div>
      </div>
    );
  }
}

export default RollDice;
