import React, { useState } from "react";
import "./Steps.css";
import man from "../../assets/man.png";
import woman from "../../assets/woman.png";
import flag from "../../assets/flag.png";
export default function Steps({ amanSteps, ansiSteps }) {
  const [amanOffset, setAmanOffset] = useState(0);
  const [amanDelta] = useState(amanSteps);
  setAmanOffset(amanOffset + amanDelta);
  return (
    <div>
      <div className="step1">
        <img
          style={{
            position: "relative",
            left: `${amanOffset}%`,
          }}
          className="profile-img "
          src={man}
          alt="Logo"
        />
        <img className="flag-img " src={flag} alt="Logo" />
      </div>
      <div className="steps-left-label">{amanSteps} steps left</div>
      <div></div>
      <br />
      <br />
      <div className="step1">
        <img className="profile-img" src={woman} alt="Logo" />
        <img className="flag-img " src={flag} alt="Logo" />
      </div>
      <div className="steps-left-label">{ansiSteps} steps left</div>
    </div>
  );
}
